import React, {useEffect, useState} from 'react';
import './styles.css';
import logo from '../../assets/logo.svg';
import { Link } from 'react-router-dom';
import { FiArrowLeft} from 'react-icons/fi'
import { Map, TileLayer, Marker } from 'react-leaflet';
import api from '../../services/api';
import axios from 'axios';

interface Item {
    id: number;
    title: string;
    image_url: string;
}

const  CreatePoint = () => {
    const [items, setItems] = useState<Item[]>([]);

    useEffect(() => {
        api.get('items').then(reponse => {
            setItems(reponse.data);
        });
    }, []);

    useEffect(() => {
        axios.get('https://servicodados.ibge.gov.br/api/v1/localidades/estados').then(reponse => {
            const ufInitials = response.data.map(uf => uf.sigla);

        });
    }, []);

    return (
         <div id="page-create-point">
             <header>
                 <img src={logo} alt="Ecoleta" />

                 <Link to="/">
                     <FiArrowLeft />
                     Voltar para home
                 </Link>
             </header>

            <form>
                <h1>Cadastro do <br/> ponto de coleta</h1>
                    <fieldset>
                        <legend>
                            <h2>Dados</h2>
                        </legend>
                     <div className="field">
                         <label htmlFor="name">Nome da entidade</label>
                         <input
                            type="text"
                            name="name"
                            id="name"
                            />
                     </div>   

                        <div className="field-group">
                        <div className="field">
                         <label htmlFor="name">E-mail</label>
                         <input
                            type="email"
                            name="email"
                            id="email"
                            />
                         </div>   

                     <div className="field">
                         <label htmlFor="Whatsapp">Whatsapp</label>
                         <input
                            type="text"
                            name="Whatsapp"
                            id="Whatsapp"
                            />
                         </div>   

                    </div>
                    </fieldset>

                    <fieldset>
                        <legend>
                            <h2>Endereço</h2>
                            <span>Selecione o endereço no mapa </span>
                        </legend>

                        <Map center={[-12.9379348, -38.4955035]}  zoom={15}>
                            <TileLayer
                                attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />

                            <Marker position={[-12.9379348, -38.4955035]} />
                        </Map>
                        
                        <div className="field-group">
                            <div className="field">
                                <label htmlFor="uf">Estado (UF)</label>
                                <select name="uf" id="uf">
                                    <option value="0">Selecione um UF</option>
                                </select>
                            </div>   
                
                            <div className="field">
                                <label htmlFor="city">Cidade</label>
                                <select name="city" id="city">
                                    <option value="0">Selecione um cidade</option>
                                </select>
                            </div>   
                        </div>   

                    </fieldset>

                    <fieldset>
                        <legend>
                            <h2>Itens de coleta</h2>
                            <span>Selecione um ou mais ítens abaixo</span>
                        </legend>

                        <ul className="items-grid">
                            {items.map(item => (
                                 <li key={item.id}>
                                 <img src={item.image_url} alt={item.title} /> 
                                 <span>{item.title}</span>
                                </li>
                            ))}
                           
                        </ul>

                    </fieldset>
                    <button type="submit">
                        Cadastrar ponto de coleta
                    </button>
            </form>
         </div>
    )
};

export default CreatePoint;
