import {useEffect, useState, ChangeEvent, FormEvent} from 'react';
import React from 'react';
import {Link, useHistory} from 'react-router-dom';
import { FiArrowLeft} from  'react-icons/fi';
import {Map, TileLayer, Marker } from 'react-leaflet';
import './styles.css';
import api from '../../services/api';
import axios from 'axios';
import logo from '../../assets/logo.svg';
import { LeafletMouseEvent } from 'leaflet'; 

interface Item {
    id: number;
    title: string;
    image_url: string;
}
interface IBGEUFResponse {
    sigla: string;
}
interface IBGECityResponse {
    nome: string;
}

const  CreatePoint = () => {
    const [items, setItems] = useState<Item[]>([]);
    const [ufs, setUfs] = useState<string[]>([]);
    const [cities, setCities] = useState<string[]>([]);
    const [initialPosition, setInitialPosition] = useState<[number, number]>([0,0]);
    const [selectedUf, setSelectedUf] = useState('0');
    const [selectedCity, setSelectCity] = useState('0');
    const [selectedPosition, setSelectendPosition] = useState<[number, number]>([0,0]);
    const [selectedItems, setSelectedItems] = useState<number[]>([]);
    const history = useHistory();

    const [formData, setForData] = useState({
        name: '',
        email: '',
        whatsapp: '',
    });

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(position => {
            const { latitude, longitude } = position.coords;

            setInitialPosition([latitude, longitude]);
        });
    }, []);


    useEffect(() => {
        api.get('items').then(response => {
            setItems(response.data);
        });
    }, []);

    useEffect(() => {
        axios
        .get<IBGEUFResponse[]>('https://servicodados.ibge.gov.br/api/v1/localidades/estados')
        .then(response => {
            const ufInitials = response.data.map(uf => uf.sigla);

            setUfs(ufInitials)
        });
    }, []);

    useEffect(() => {
        if (selectedUf === '0'){
            return;
        }
 

        axios
        .get<IBGECityResponse[]>(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${selectedUf}/municipios`)
        .then(response => {
            const cityNames = response.data.map(city => city.nome);
            setCities(cityNames);
        });
    }, [selectedUf]);

    function handleSelectUF(event: ChangeEvent<HTMLSelectElement>){
        const uf = event.target.value;
        setSelectedUf(uf);
    }

    function handleSelectCity(event: ChangeEvent<HTMLSelectElement>){
        const city = event.target.value;
        setSelectedUf(city);
    }

    function handleMapClick(event: LeafletMouseEvent) {
        setSelectendPosition([
            event.latlng.lat,
            event.latlng.lng,
        ])
    }
    
    function handleInputChage(event: ChangeEvent<HTMLInputElement>){
        const { name, value } = event.target;

        setForData({ ...formData, [name]: value})
    }

    function handleSelectItem(id: number) {
        const alreadySelected = selectedItems.findIndex(item => item === id);
        
        if (alreadySelected >= 0) {
            const filteredItems = selectedItems.filter(item => item !== id)
        } else {
            setSelectedItems([ ...selectedItems, id ]);
        }     
    }

   async function handleSubmit(event:FormEvent) {
        event.preventDefault();

        const { name, email, whatsapp } = formData;
        const uf = selectedUf;
        const city = selectedCity;
        const items = selectedItems;
        const [latitude, longitude] = selectedPosition;

        const data = {
            name,
            email,
            whatsapp,
            uf,
            city,
            latitude,
            longitude,
            items
        };

       await api.post('points', data)

       alert('Ponto de coleta criado!');
       history.push('/');
    }

    return (
         <div id="page-create-point">
             <header>
                 <img src={logo} alt="Ecoleta" />

                 <Link to="/">
                     <FiArrowLeft />
                     Voltar para home
                 </Link>
             </header>

            <form onSubmit={handleSubmit}>
                <h1>Cadastro do <br/> ponto de coleta</h1>
                    <fieldset>
                        <legend>
                            <h2>Dados</h2>
                        </legend>
                     <div className="field">
                         <label htmlFor="name">Nome da entidade</label>
                         <input
                            type="text"
                            name="name"
                            id="name"
                            onChange={handleInputChage}
                            />
                     </div>   

                        <div className="field-group">
                        <div className="field">
                         <label htmlFor="name">E-mail</label>
                         <input
                            type="email"
                            name="email"
                            id="email"
                            onChange={handleInputChage}
                            />
                         </div>   

                     <div className="field">
                         <label htmlFor="whatsapp">Whatsapp</label>
                         <input
                            type="text"
                            name="whatsapp"
                            id="whatsapp"
                            onChange={handleInputChage}
                            />
                         </div>   

                    </div>
                    </fieldset>

                    <fieldset>
                        <legend>
                            <h2>Endereço</h2>
                            <span>Selecione o endereço no mapa </span>
                        </legend>

                        <Map center={initialPosition}  zoom={15} onclick={handleMapClick}>
                            <TileLayer
                                attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />

                            <Marker position={selectedPosition} />
                        </Map>
                        
                        <div className="field-group">
                            <div className="field">
                                <label htmlFor="uf">Estado (UF)</label>
                                <select name="uf" id="uf" value={selectedUf} onChange={handleSelectUF}>
                                    <option value="0">Selecione um UF</option>
                                    {ufs.map(uf => (
                                        <option key={uf} value={uf}>{uf}</option>
                                    ))}
                                </select>
                            </div>   
                
                            <div className="field">
                                <label htmlFor="city">Cidade</label>
                                <select 
                                name="city" 
                                id="city">
                                value={selectedCity}
                                onChange={handleSelectCity}    
                                    <option value="0">Selecione um cidade</option>
                                    {cities.map(city => (
                                        <option key={String(city)} value={String(city)}>{String(city)}</option>
                                    ))}
                                </select>
                            </div>   
                        </div>   

                    </fieldset>

                    <fieldset>
                        <legend>
                            <h2>Itens de coleta</h2>
                            <span>Selecione um ou mais ítens abaixo</span>
                        </legend>

                        <ul className="items-grid">
                            {items.map(item => (
                                 <li key={item.id}
                                 onClick={() => handleSelectItem(item.id)}
                                 className={selectedItems.includes(item.id) ? 'selected' : ''}  >  
                                 <img src={item.image_url} alt={item.title} /> 
                                 <span>{item.title}</span>
                                </li>
                            ))}
                           
                        </ul>

                    </fieldset>
                    <button type="submit">
                        Cadastrar ponto de coleta
                    </button>
            </form>
         </div>
        );
     };
export default CreatePoint;
